using System;using System.Linq;using System.Text;class P { static void Main() { Console.OutputEncoding = Encoding.UTF8; var cakes = new[] { new { Flavor = "Ванільний", Calories = 250, Price = 5.0, Stock = 10 }, new { Flavor = "Шоколадний", Calories = 300, Price = 6.0, Stock = 5 }, new { Flavor = "Полуничний", Calories = 230, Price = 4.5, Stock = 8 }, new { Flavor = "Лимонний", Calories = 220, Price = 4.0, Stock = 12 }, new { Flavor = "Червоний оксамит", Calories = 350, Price = 7.0, Stock = 7 } }; var totalCakes = cakes.Sum(c => c.Stock); var totalCalories = cakes.Sum(c => c.Calories * c.Stock); var uniqueFlavors = cakes.Select(c => c.Flavor).Distinct().Count(); var ch = cakes.FirstOrDefault(c => c.Flavor == "Шоколадний"); var highest = cakes.OrderByDescending(c => c.Calories).First(); Console.WriteLine($"Загальна кількість: {totalCakes}, Калорії: {totalCalories}, Унікальні смаки: {uniqueFlavors}, Шоколадні: {ch?.Stock},Maxкількість калорій: {highest.Flavor} ({highest.Calories})"); } }
